// Extract message data from WhatsApp webhook
const body = items[0].json;

// Check if this is a message event
if (!body.entry || !body.entry[0].changes[0].value.messages) {
  return [{json: {skip: true}}];
}

const entry = body.entry[0];
const changes = entry.changes[0];
const value = changes.value;
const message = value.messages[0];
const contact = value.contacts[0];

return [{
  json: {
    message_id: message.id,
    from: message.from,
    timestamp: message.timestamp,
    type: message.type,
    text: message.text ? message.text.body : '',
    customer_name: contact.profile.name,
    customer_phone: contact.wa_id,
    phone_number_id: value.metadata.phone_number_id,
    display_phone_number: value.metadata.display_phone_number
  }
}];

// Prepare data for AI classification
const input = items[0].json;

return [{
  json: {
    ...input,
    prompt: `Classify this WhatsApp message: "${input.text}"

Categories:
- order: wants to place an order
- greeting: saying hello
- question: asking about products/prices
- confirmation: confirming something (s√≠, dale)
- lo_de_siempre: wants their usual order
- other: none of above

Respond with JSON only: {"intent": "category", "confidence": 0.9}`
  }
}];

// Parse AI response and route
const aiResponse = items[0].json.message.content;
const originalData = items[0].json;

let parsed;
try {
  parsed = JSON.parse(aiResponse);
} catch(e) {
  parsed = {intent: 'other', confidence: 0};
}

return [{
  json: {
    intent: parsed.intent,
    confidence: parsed.confidence,
    customer_phone: originalData.customer_phone || '',
    customer_name: originalData.customer_name || '',
    text: originalData.text || '',
    phone_number_id: originalData.phone_number_id || ''
  }
}];
